import React from "react";
import { Col, Form, Row } from "react-bootstrap";
//STYLE
import "bootstrap/dist/css/bootstrap.min.css";
import "./assets/css/style.css";

export default class App extends React.Component {
  constructor() {
    super();
    this.state = {
      firstName: "",
      lastName: "",
      title: "",
      phone: "",
      email: "",
      street: "",
      city: "",
      zip: "",
    };
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(e) {
    const { name, value, type, checked } = e.target;
    type === "checkbox"
      ? this.setState({ [name]: checked })
      : this.setState({ [name]: value });
  }
  // componentDidMount() {
  //   fetch("https://restcountries.eu/rest/v2/all")
  //     .then((response) => response.json())
  //     .then((data) => {
  //       this.setState({
  //         countries: data,
  //       });
  //     });
  // }
  render() {
    return (
      <div className="container-fluid">
        <div className="row no-gutters">
          <div className="col no-gutters">
            <div className="leftside">
              <Form>
                <h3 className="text-center mb-4">Vos Informations</h3>
                <Row>
                  <Col className="mb-4">
                    <Form.Control
                      type="text"
                      name="firstName"
                      placeholder="prénom"
                      size="sm"
                      value={this.state.firstName}
                      onChange={this.handleChange}
                    />
                  </Col>
                  <Col>
                    <Form.Control
                      type="text"
                      name="lastName"
                      placeholder="nom"
                      size="sm"
                      value={this.state.lastName}
                      onChange={this.handleChange}
                    />
                  </Col>
                  <Col className="mb-4">
                    <Form.Control
                      type="text"
                      name="title"
                      placeholder="titre"
                      size="sm"
                      value={this.state.title}
                      onChange={this.handleChange}
                    />
                  </Col>
                </Row>
                <h5 className="mb-4">Coordonnées</h5>
                <Row>
                  <Col className="mb-4">
                    <Form.Control
                      type="tel"
                      name="phone"
                      placeholder="téléphone"
                      value={this.state.phone}
                      onChange={this.handleChange}
                    />
                  </Col>
                  <Col>
                    <Form.Control
                      type="email"
                      name="email"
                      placeholder="email"
                      value={this.state.email}
                      onChange={this.handleChange}
                    />
                  </Col>
                  <Col>
                    <Form.Control
                      type="text"
                      name="street"
                      placeholder="rue"
                      value={this.state.street}
                      onChange={this.handleChange}
                    />
                  </Col>
                  <Col>
                    <Form.Control
                      type="text"
                      name="city"
                      placeholder="ville"
                      value={this.state.city}
                      onChange={this.handleChange}
                    />
                  </Col>
                  <Col>
                    <Form.Control
                      type="text"
                      name="zip"
                      placeholder="code postal"
                      value={this.state.zip}
                      onChange={this.handleChange}
                    />
                  </Col>
                </Row>{" "}
                {/* <h5 className="mb-4">Réseaux sociaux</h5>
                <Row>
                  <Col>
                  </Col>
                </Row> */}
              </Form>
            </div>
          </div>
          <div className="col no-gutters">
            <div className="rightside">
              <div className="document">
                {" "}
                <div className="infos">
                  {this.state.firstName ? (
                    <h3>{this.state.firstName} </h3>
                  ) : null}
                  {this.state.lastName ? <h3>{this.state.lastName}</h3> : null}
                  {this.state.title ? <h3>{this.state.title}</h3> : null}
                </div>
                <div className="contact">
                  {this.state.phone ? <h3>{this.state.phone}</h3> : null}
                  {this.state.email ? <h3>{this.state.email}</h3> : null}
                  {this.state.street ? <h3>{this.state.street}</h3> : null}
                  {this.state.city ? <h3>{this.state.city}</h3> : null}
                  {this.state.zip ? <h3>{this.state.zip}</h3> : null}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
