import React from "react";
import { Col, Form, Row } from "react-bootstrap";
//STYLE
import "bootstrap/dist/css/bootstrap.min.css";
import "./assets/css/style.css";

export default class App extends React.Component {
  constructor() {
    super();
    this.state = {
      firstName: "",
      lastName: "",
      title:"",
      phone:"",
      email:"",
      street:"",
      city:"",
      zip:"",

    };
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(e) {
    const { name, value, type, checked } = e.target;
    type === "checkbox"
      ? this.setState({ [name]: checked })
      : this.setState({ [name]: value });
  }
  // componentDidMount() {
  //   fetch("https://restcountries.eu/rest/v2/all")
  //     .then((response) => response.json())
  //     .then((data) => {
  //       this.setState({
  //         countries: data,
  //       });
  //     });
  // }
  render() {
    return (
      <div className="container-fluid">
        <div className="row no-gutters">
          <div className="col no-gutters">
            <div className="leftside">
              <Form>
                <Row>
                  <Col>
                  <Form.Control
                    type="text"
                    name="firstName"
                    placeholder="Prénom"
                    size="lg"
                    value={this.state.firstName}
                    onChange={this.handleChange}
                  />
                  </Col>
                  <Col>
                  <Form.Control
                    type="text"
                    name="lastName"
                    placeholder="Nom"
                    size="lg"
                    value={this.state.lastName}
                    onChange={this.handleChange}
                  />
                  </Col>
                </Row>
                <Form.Group controlId="exampleForm.ControlInput1">
                  <Form.Label>Prénom</Form.Label>

                    <Form.Label>Nom</Form.Label>

                </Form.Group>
                <Form.Group controlId="exampleForm.ControlInput2">

                </Form.Group>
                <Form.Group controlId="exampleForm.ControlInput3">
                  <Form.Label>Titre</Form.Label>
                  <Form.Control
                    type="text"
                    name="title"
                    value={this.state.title}
                    onChange={this.handleChange}
                  />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlInput3">
                  <Form.Label>Téléphone</Form.Label>
                  <Form.Control
                    type="text"
                    name="phone"
                    value={this.state.phone}
                    onChange={this.handleChange}
                  />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlInput3">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="text"
                    name="email"
                    value={this.state.email}
                    onChange={this.handleChange}
                  />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlInput3">
                  <Form.Label>Rue</Form.Label>
                  <Form.Control
                    type="text"
                    name="street"
                    value={this.state.street}
                    onChange={this.handleChange}
                  />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlInput3">
                  <Form.Label>Ville</Form.Label>
                  <Form.Control
                    type="text"
                    name="city"
                    value={this.state.city}
                    onChange={this.handleChange}
                  />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlInput3">
                  <Form.Label>Code postal</Form.Label>
                  <Form.Control
                    type="text"
                    name="zip"
                    value={this.state.zip}
                    onChange={this.handleChange}
                  />
                </Form.Group>
                {/* <Form.Group controlId="exampleForm.ControlInput3">
                  <Form.Label>Age</Form.Label>
                  <Form.Control
                    type="number"
                    name="age"
                    value={this.state.age}
                    onChange={this.handleChange}
                  />
                </Form.Group> */}

                {/* <Form.Group controlId="exampleForm.ControlCheckbox">
                  <h4>Are you my friend ?</h4>
                  <Form.Label>check if you do !</Form.Label>
                  <Form.Control
                    type="checkbox"
                    name="isFriendly"
                    checked={this.state.isFriendly}
                    onChange={this.handleChange}
                  />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlRadios">
                  <h4>Your gender please</h4>
                  <Form.Label>Male</Form.Label>
                  <Form.Control
                    type="radio"
                    name="gender"
                    value="male"
                    checked={this.state.gender === "male"}
                    onChange={this.handleChange}
                  />
                  <Form.Label>female</Form.Label>
                  <Form.Control
                    type="radio"
                    name="gender"
                    value="female"
                    checked={this.state.gender === "female"}
                    onChange={this.handleChange}
                  />
                </Form.Group> */}
                {/* <Form.Group controlId="exampleForm.ControlSelect1">
                  <Form.Label>Choose your country</Form.Label>
                  <Form.Control
                    as="select"
                    value={this.state.country}
                    name="country"
                    onChange={this.handleChange}
                  >
                    {this.state.countries.map((country) => (
                      <option key={country.name} value={country.name}>
                        {country.name}
                      </option>
                    ))}
                  </Form.Control>
                </Form.Group> */}
              </Form>
            </div>
          </div>
          <div className="col no-gutters">
            <div className="rightside">
              <div className="document">
                {" "}
                <p>
                  {this.state.firstName ? (
                    <h3>{this.state.firstName} </h3>
                  ) : null}
                  {this.state.lastName ? <h3>{this.state.lastName}</h3> : null}
                  {this.state.title ? <h3>{this.state.title}</h3> : null}
                  {this.state.phone ? <h3>{this.state.phone}</h3> : null}
                  {this.state.email ? <h3>{this.state.email}</h3> : null}
                  {this.state.street ? <h3>{this.state.street}</h3> : null}
                  {this.state.city ? <h3>{this.state.city}</h3> : null}
                  {this.state.zip ? <h3>{this.state.zip}</h3> : null}

                  {/* {this.state.age ? <h3>You are a {this.state.age} years old</h3> : null}
          {this.state.gender !== '' ? <h2> {this.state.gender}</h2> : null}
          {this.state.country !== '' ? (
            <h2> from {this.state.country}</h2>
          ) : null} */}
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
