import React from "react";
import { Col, Form, Row } from "react-bootstrap";

//STYLE
import "bootstrap/dist/css/bootstrap.min.css";
import "./assets/css/style.css";

export default class App extends React.Component {
  constructor() {
    super();
    this.state = {
      firstName: "",
      lastName: "",
      title: "",
      phone: "",
      email: "",
      number: "",
      street: "",
      city: "",
      zip: "",
      educationSchool: "",
      educationDegree: "",
      educationYear: "",
      educationItems: [],
      jobCompany: "",
      jobTitle: "",
      jobYear: "",
      jobItems: [],
    };
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(e) {
    const { name, value, type, checked } = e.target;
    type === "checkbox"
      ? this.setState({ [name]: checked })
      : this.setState({ [name]: value });
  }

  handleEducationSubmit = (e) => {
    e.preventDefault();
    this.setState({
      educationSchool: "",
      educationDegree: "",
      educationYear: "",
      educationItems: [
        ...this.state.educationItems,
        {
          educationSchool: this.state.educationSchool,
          educationDegree: this.state.educationDegree,
          educationYear: this.state.educationYear,
        },
      ],
    });
  };
  deleteEducation = (index) => {
    const arr = this.state.educationItems;
    arr.splice(index, 1);
    this.setState({
      educationItems: arr,
    });
  };
  educationList = () => {
    return this.state.educationItems.map((educationItem, index) => {
      return (
        <div className="card mb-3" key={index}>
          <i
            className="fas fa-times"
            style={{ cursor: "pointer", float: "right", color: "red" }}
            onClick={() => this.deleteEducation(index)}
          ></i>
          <div className="card-body">
            <h4>{educationItem.educationSchool}</h4>
