import React from "react";
import { jsPDF } from "jspdf";

//STYLE
import "./assets/css/style.css";
import GenericPdfDownloader from "./components/GenereicPdfDownloader";
export default class App extends React.Component {
  constructor() {
    super();
    this.state = {
      firstName: "",
      lastName: "",
      title: "",
      phone: "",
      email: "",
      streetNumber: "",
      street: "",
      city: "",
      zip: "",
      educationSchool: "",
      educationDegree: "",
      educationYear: "",
      educationItems: [],
      jobCompany: "",
      jobTitle: "",
      jobYear: "",
      jobItems: [],
    };
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(e) {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  }

  handleEducationSubmit = (e) => {
    e.preventDefault();
    this.setState({
      educationSchool: "",
      educationDegree: "",
      educationYear: "",
      educationItems: [
        ...this.state.educationItems,
        {
          educationSchool: this.state.educationSchool,
          educationDegree: this.state.educationDegree,
          educationYear: this.state.educationYear,
        },
      ],
    });
  };
  deleteEducation = (index) => {
    const arr = this.state.educationItems;
    arr.splice(index, 1);
    this.setState({
      educationItems: arr,
    });
  };
  educationList = () => {
    return this.state.educationItems.map((educationItem, index) => {
      return (
        <div className="education-card" key={index}>
          <i
            className="fas fa-times"
            onClick={() => this.deleteEducation(index)}
          ></i>
          <div className="education-infos">
            <h4>{educationItem.educationSchool}</h4>
            <h4>{educationItem.educationDegree}</h4>
            <h4>{educationItem.educationYear}</h4>
          </div>
        </div>
      );
    });
  };

  handleJobSubmit = (e) => {
    e.preventDefault();
    this.setState({
      jobCompany: "",
      jobTitle: "",
      jobYear: "",
      jobItems: [
        ...this.state.jobItems,
        {
          jobCompany: this.state.jobCompany,
          jobTitle: this.state.jobTitle,
          jobYear: this.state.jobYear,
        },
      ],
    });
  };
  deleteJob = (index) => {
    const arr = this.state.jobItems;
    arr.splice(index, 1);
    this.setState({
      jobItems: arr,
    });
  };

  jobList = () => {
    return this.state.jobItems.map((jobItem, index) => {
      return (
        <div className="job-card" key={index}>
          <i className="fas fa-times" onClick={() => this.deleteJob(index)}></i>
          <div className="job-infos">
            <h4>{jobItem.jobCompany}</h4>
            <h4>{jobItem.jobTitle}</h4>
            <h4>{jobItem.jobYear}</h4>
          </div>
        </div>
      );
    });
  };
  downloadPdfDocument = () => {
    const doc = new jsPDF("p", "pt", "a4");
    doc.html(document.querySelector("#documentToDownload"), {
      callback: function (pdf) {
        pdf.save("rezume_me.pdf");
      },
    });
  };
  // componentDidMount() {
  //   fetch("https://restcountries.eu/rest/v2/all")
  //     .then((response) => response.json())
  //     .then((data) => {
  //       this.setState({
  //         countries: data,
  //       });
  //     });
  // }
  render() {
    return (
      <div className="container">
        <div className="leftside">
          <div className="form">
            <form>
              <h3>Vos Informations</h3>
              <div className="form-infos">
                <input
                  type="text"
                  name="firstName"
                  placeholder="prénom"
                  value={this.state.firstName}
                  onChange={this.handleChange}
                />
                <input
                  type="text"
                  name="lastName"
                  placeholder="nom"
                  value={this.state.lastName}
                  onChange={this.handleChange}
                />
                <input
                  type="text"
                  name="title"
                  placeholder="titre"
                  value={this.state.title}
                  onChange={this.handleChange}
                />
              </div>
              <h5>coordonnées</h5>
              <div className="form-contact">
                <input
                  type="tel"
                  name="phone"
                  placeholder="téléphone"
                  value={this.state.phone}
                  onChange={this.handleChange}
                />
                <input
                  type="email"
                  name="email"
                  placeholder="email"
                  value={this.state.email}
                  onChange={this.handleChange}
                />
              </div>
              <h5>Votre adresse</h5>
              <div className="form-address">
                <input
                  type="text"
                  name="streetNumber"
                  placeholder="numéro"
                  value={this.state.streetNumber}
                  onChange={this.handleChange}
                />
                <input
                  type="text"
                  name="street"
                  placeholder="rue"
                  value={this.state.street}
                  onChange={this.handleChange}
                />
                <input
                  type="text"
                  name="zip"
                  placeholder="code postal"
                  value={this.state.zip}
                  onChange={this.handleChange}
                />
                <input
                  type="text"
                  name="city"
                  placeholder="ville"
                  value={this.state.city}
                  onChange={this.handleChange}
                />{" "}
              </div>
            </form>

            <form onSubmit={this.handleEducationSubmit}>
              <h5 className="degrees-title">Vos diplomes/formations</h5>
              <div className="form-degrees">
                <input
                  type="text"
                  name="educationSchool"
                  placeholder="école..."
                  value={this.state.educationSchool}
                  onChange={this.handleChange}
                />

                <input
                  type="text"
                  name="educationDegree"
                  placeholder="diplome"
                  value={this.state.educationDegree}
                  onChange={this.handleChange}
                />

                <input
                  type="text"
                  name="educationYear"
                  placeholder="année"
                  value={this.state.educationYear}
                  onChange={this.handleChange}
                />
              </div>
              <button className="degrees-button">ajouter formation</button>
            </form>

            <form onSubmit={this.handleJobSubmit}>
              <h5 className="jobs-title">Vos expériences professionnelles</h5>
              <div className="form-jobs">
                <input
                  type="text"
                  name="jobCompany"
                  placeholder="entreprise"
                  value={this.state.jobCompany}
                  onChange={this.handleChange}
                />

                <input
                  type="text"
                  name="jobTitle"
                  placeholder="intitulé du poste"
                  value={this.state.jobTitle}
                  onChange={this.handleChange}
                />

                <input
                  type="text"
                  name="jobYear"
                  placeholder="année"
                  value={this.state.jobYear}
                  onChange={this.handleChange}
                />
              </div>
              <button className="jobs-button">ajouter poste</button>
            </form>
          </div>
        </div>
        <div className="rightside">
          <div id="documentToDownload" className="document">
            <div id="infos" className="infos">
              {this.state.firstName ? (
                <h2>
                  {this.state.firstName} {this.state.lastName}{" "}
                </h2>
              ) : null}
              {/* {this.state.lastName ? <h2></h2> : null} */}

              {this.state.title ? <h4>{this.state.title}</h4> : null}
            </div>
            <div id="contact" className="contact">
              {this.state.phone ? (
                <div>
                  <h3>
                    <span>
                      <i className="fas fa-phone-alt"></i>
                    </span>
                    {this.state.phone}
                  </h3>
                </div>
              ) : null}
              {this.state.email ? (
                <h3>
                  <span>
                    <i className="fas fa-envelope"></i>
                  </span>
                  {this.state.email}
                </h3>
              ) : null}
            </div>
            <div id="address" className="address">
              {this.state.streetNumber ? (
                <h3>{this.state.streetNumber}</h3>
              ) : null}
              {this.state.street ? <h3>{this.state.street}</h3> : null}
              {this.state.zip ? <h3>{this.state.zip}</h3> : null}
              {this.state.city ? <h3>{this.state.city}</h3> : null}
            </div>
            <div id="education" className="education">
              {this.educationList()}
            </div>
            <div id="job" className="job">
              {this.jobList()}
            </div>
          </div>
          <div className="download">
            <button onClick={this.downloadPdfDocument}>Télécharger PDF</button>
            {/* <GenericPdfDownloader
              downloadFileName="rezume_me_pdf"
              rootElementId="documentToDownload"
            /> */}
          </div>
        </div>
      </div>
    );
  }
}
