import React from "react";
import { jsPDF } from "jspdf";

//STYLE
import "./assets/css/style.css";
import GenericPdfDownloader from "./components/GenereicPdfDownloader";
export default class App extends React.Component {
  constructor() {
    super();
    this.state = {
      firstName: "",
      lastName: "",
      title: "",
      phone: "",
      email: "",
      streetNumber: "",
      street: "",
      city: "",
      zip: "",
      educationSchool: "",
      educationDegree: "",
      educationYear: "",
      educationItems: [],
      jobCompany: "",
      jobTitle: "",
      jobYear: "",
      jobItems: [],
      educationCount:"",
      isEducated: false,
      isEmployed: false,
    };
    // this.handleChange = this.handleChange.bind(this);
    // this.deleteEducation = this.deleteEducation.bind(this);
    // this.deleteJob= this.deleteJob.bind(this);
    // this.handleEducationSubmit = this.handleEducationSubmit.bind(this);
    // this.handleJobSubmit = this.handleJobSubmit.bind(this);
  }
  handleChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  }

  handleEducationSubmit = (e) => {
    e.preventDefault();
    this.setState({
      educationSchool: "",
      educationDegree: "",
      educationYear: "",
      educationItems: [
        ...this.state.educationItems,
        {
          educationSchool: this.state.educationSchool,
          educationDegree: this.state.educationDegree,
          educationYear: this.state.educationYear,
        },
      ],
    });
  };
  deleteEducation = (index) => {
    const arr = this.state.educationItems;
    arr.splice(index, 1);
    this.setState({
      educationItems: arr,
    });
  };

  educationCounter = () => {
    this.setState({
      isEducated: !this.state.isEducated
    })
  //   this.setState(prevState =>{
  //     return{
  //          ...prevState,
  //          educationCount : prevState.educationCount +1
  //     }
  //  })
  }
  educationList = () => {
    return this.state.educationItems.map((educationItem, index) => {
      return (
        <div className="education-card" key={index}>
          <h5>Diplome numéro {index + 1}</h5>
          <i
            className="fas fa-times"
            onClick={() => this.deleteEducation(index)}
          ></i>
          <div className="education-infos">
            <h4>{educationItem.educationSchool}</h4>
            <h4>{educationItem.educationDegree}</h4>
            <h4>{educationItem.educationYear}</h4>
          </div>
        </div>
      );
    });
  };

  handleJobSubmit = (e) => {
    e.preventDefault();
    this.setState({
      jobCompany: "",
      jobTitle: "",
      jobYear: "",
      jobItems: [
        ...this.state.jobItems,
        {
          jobCompany: this.state.jobCompany,
          jobTitle: this.state.jobTitle,
          jobYear: this.state.jobYear,
        },
      ],
    });
  };
  deleteJob = (index) => {
    const arr = this.state.jobItems;
    arr.splice(index, 1);
    this.setState({
      jobItems: arr,
    });
  };

  jobList = () => {
    return this.state.jobItems.map((jobItem, index) => {
      return (
        <div className="job-card" key={index}>
          <i className="fas fa-times" onClick={() => this.deleteJob(index)}></i>
          <div className="job-infos">
            <h4>{jobItem.jobCompany}</h4>
            <h4>{jobItem.jobTitle}</h4>
            <h4>{jobItem.jobYear}</h4>
          </div>
        </div>
      );
    });
  };
  downloadPdfDocument = () => {
    const doc = new jsPDF("p", "pt", "a4");
