import React from "react";
import { Col, Form, Row } from "react-bootstrap";
//STYLE
import "bootstrap/dist/css/bootstrap.min.css";
import "./assets/css/style.css";
import Education from "./components/Education";

export default class App extends React.Component {
  constructor() {
    super();
    this.state = {
      firstName: "",
      lastName: "",
      title: "",
      phone: "",
      email: "",
      number: "",
      street: "",
      city: "",
      zip: "",
      education: '',
      educationItems: [],
    };
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(e) {
    const { name, value, type, checked } = e.target;
    type === "checkbox"
      ? this.setState({ [name]: checked })
      : this.setState({ [name]: value });
  }

  handleEducationSubmit = (e) => {
    e.preventDefault()
    this.setState({
      education: '',
      educationItems: [...this.state.educationItems, { education: this.state.education }],
    })
  }

  educationList = () => {
    return this.state.educationItems.map((educationItem, index) => {
      return (
        <div className="card mb-3" key={index}>
          <div className="card-body">
            <h4>
              {educationItem.education}
              <i
                class="fas fa-times"
                style={{ cursor: 'pointer', float: 'right', color: 'red' }}
                onClick={() => this.deleteTask(index)}
              ></i>
            </h4>
          </div>
        </div>
      )
    })
  }
  deleteEducation= (index) => {
const arr = this.state.educationItems;
arr.splice(index, 1);
this.setState({
    educationItems: arr
})
}
  // componentDidMount() {
  //   fetch("https://restcountries.eu/rest/v2/all")
  //     .then((response) => response.json())
  //     .then((data) => {
  //       this.setState({
  //         countries: data,
  //       });
  //     });
  // }
  render() {
    return (
      <div className="container-fluid">
        <div className="row no-gutters">
          <div className="col no-gutters">
            <div className="leftside">
              <Form>
                <h3 className="text-center mb-4">Vos Informations</h3>
                <Row>
                  <Col className="mb-4">
                    <Form.Control
                      type="text"
                      name="firstName"
                      placeholder="prénom"
                      size="sm"
                      value={this.state.firstName}
                      onChange={this.handleChange}
                    />
                  </Col>
                  <Col>
                    <Form.Control
                      type="text"
                      name="lastName"
                      placeholder="nom"
                      size="sm"
                      value={this.state.lastName}
                      onChange={this.handleChange}
                    />
                  </Col>
                  <Col className="mb-4">
                    <Form.Control
                      type="text"
                      name="title"
                      placeholder="titre"
                      size="sm"
                      value={this.state.title}
                      onChange={this.handleChange}
                    />
                  </Col>
                </Row>
                <h5 className="mb-4">Coordonnées</h5>
                <Row>
                  <Col className="mb-4">
                    <Form.Control
                      type="tel"
                      name="phone"
                      placeholder="téléphone"
                      value={this.state.phone}
                      onChange={this.handleChange}
                    />
                  </Col>
                  <Col>
                    <Form.Control
                      type="email"
                      name="email"
                      placeholder="email"
                      value={this.state.email}
                      onChange={this.handleChange}
                    />
                  </Col>
                </Row>
                <Row>
                  <Col>
                    <Form.Control
                      type="text"
                      name="number"
                      placeholder="numéro"
                      value={this.state.number}
                      onChange={this.handleChange}
                    />
                  </Col>
                  <Col>
                    <Form.Control
                      type="text"
                      name="street"
                      placeholder="rue"
                      value={this.state.street}
                      onChange={this.handleChange}
                    />
                  </Col>
                  <Col>
                    <Form.Control
                      type="text"
                      name="zip"
                      placeholder="code postal"
                      value={this.state.zip}
                      onChange={this.handleChange}
                    />
                  </Col>
                  <Col>
                    <Form.Control
                      type="text"
                      name="city"
                      placeholder="ville"
                      value={this.state.city}
                      onChange={this.handleChange}
                    />
                  </Col>
                </Row>{" "}
                {/* <h5 className="mb-4">Réseaux sociaux</h5>
                <Row>
                  <Col>
                  </Col>
                </Row> */}
              </Form>
              <div className="card my-3">
              <div className="card-header">Formation</div>
              <div className="card-body">
                <Form onSubmit={this.onSubmit}>
                  <Row>
                    <Col>
                    <label htmlFor="element">Task</label>
                    <input
                      type="text"
                      className="form-control form-control-lg"
                      name="element"
                      onChange={this.onChange}
                      value={this.state.element}
                    />
                    </Col>
                  </Row>
                  <button className="btn btn-primary btn-block">ajouter formation</button>
                </Form>
              </div>
            </div>
              {/* <Education /> */}
            </div>
          </div>
          <div className="col no-gutters">
            <div className="rightside">
              <div className="document">
                {" "}
                <div className="infos">
                  {this.state.firstName ? (
                    <h3>{this.state.firstName} </h3>
                  ) : null}
                  {this.state.lastName ? <h3>{this.state.lastName}</h3> : null}
                  {this.state.title ? <h3>{this.state.title}</h3> : null}
                </div>
                <div className="contact">
                  {this.state.phone ? (
                    <div>
                      <h3>
                        <span>
                          <i class="fas fa-phone-alt"></i>
                        </span>
                        {this.state.phone}
                      </h3>
                    </div>
                  ) : null}
                  {this.state.email ? (
                    <h3>
                      <span>
                        <i class="fas fa-envelope"></i>
                      </span>
                      {this.state.email}
                    </h3>
                  ) : null}
                </div>
                <div className="address">
                  {this.state.number ? <h3>{this.state.number}</h3> : null}
                  {this.state.street ? <h3>{this.state.street}</h3> : null}
                  {this.state.zip ? <h3>{this.state.zip}</h3> : null}
                  {this.state.city ? <h3>{this.state.city}</h3> : null}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
