import React from "react";
import { Form } from "react-bootstrap";
//STYLE
import "bootstrap/dist/css/bootstrap.min.css";
import "./assets/css/style.css";

export default class App extends React.Component {
  constructor() {
    super();
    this.state = {
      firstName: "",
      lastName: "",
      age: "",
      isfriendly: null,
      gender: "",
      countries: [],
      country: "",
    };
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(e) {
    const { name, value, type, checked } = e.target;
    type === "checkbox"
      ? this.setState({ [name]: checked })
      : this.setState({ [name]: value });
  }
  componentDidMount() {
    fetch("https://restcountries.eu/rest/v2/all")
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          countries: data,
        });
      });
  }
  render() {
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col">
            <div className="leftside">
              <Form>
                <Form.Group controlId="exampleForm.ControlInput1">
                  <Form.Label>First Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="firstName"
                    value={this.state.firstName}
                    onChange={this.handleChange}
                  />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlInput2">
                  <Form.Label>Last Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="lastName"
                    value={this.state.lastName}
                    onChange={this.handleChange}
                  />
                </Form.Group>
                {/* <Form.Group controlId="exampleForm.ControlInput3">
                  <Form.Label>Age</Form.Label>
                  <Form.Control
                    type="number"
                    name="age"
                    value={this.state.age}
                    onChange={this.handleChange}
                  />
                </Form.Group> */}

                {/* <Form.Group controlId="exampleForm.ControlCheckbox">
                  <h4>Are you my friend ?</h4>
                  <Form.Label>check if you do !</Form.Label>
                  <Form.Control
                    type="checkbox"
                    name="isFriendly"
                    checked={this.state.isFriendly}
                    onChange={this.handleChange}
                  />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlRadios">
                  <h4>Your gender please</h4>
                  <Form.Label>Male</Form.Label>
                  <Form.Control
                    type="radio"
                    name="gender"
                    value="male"
                    checked={this.state.gender === "male"}
                    onChange={this.handleChange}
                  />
                  <Form.Label>female</Form.Label>
                  <Form.Control
                    type="radio"
                    name="gender"
                    value="female"
                    checked={this.state.gender === "female"}
                    onChange={this.handleChange}
                  />
                </Form.Group> */}
                {/* <Form.Group controlId="exampleForm.ControlSelect1">
                  <Form.Label>Choose your country</Form.Label>
                  <Form.Control
                    as="select"
                    value={this.state.country}
                    name="country"
                    onChange={this.handleChange}
                  >
                    {this.state.countries.map((country) => (
                      <option key={country.name} value={country.name}>
                        {country.name}
                      </option>
                    ))}
                  </Form.Control>
                </Form.Group> */}
              </Form>
            </div>
          </div>
          <div className="col">
            <div className="rightside">
              <div className="document"> <p>

          {this.state.firstName ? (
            <h3>{this.state.firstName} </h3>
          ) : null}
          {this.state.lastName ? (
            <h3>{this.state.lastName}</h3>
          ) : null}
          {this.state.age ? <h3>You are a {this.state.age} years old</h3> : null}
          {this.state.gender !== '' ? <h2> {this.state.gender}</h2> : null}
          {this.state.country !== '' ? (
            <h2> from {this.state.country}</h2>
          ) : null}
        </p></div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
