import React from "react";
import ReactDOM from "react-dom";
import Pdf from "react-to-pdf";
// import { jsPDF } from "jspdf";

//STYLE
import "./assets/css/style.css";
// import GenericPdfDownloader from "./components/GenericPdfDownloader";

const ref = React.createRef();
export default class App extends React.Component {
  constructor() {
    super();
    this.state = {
      firstName: "",
      lastName: "",
      title: "",
      phone: "",
      email: "",
      address: "",
      city: "",
      zip: "",
      educationSchool: "",
      educationDegree: "",
      educationYear: "",
      educationItems: [],
      jobCompany: "",
      jobTitle: "",
      jobYear: "",
      jobItems: [],
      educationCount: 0,
      jobCount: 0,
    };
  }
  handleChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  handleEducationSubmit = (e) => {
    e.preventDefault();
    this.educationCounterIncrease();
    this.setState({
      educationSchool: "",
      educationDegree: "",
      educationYear: "",
      educationItems: [
        ...this.state.educationItems,
        {
          educationSchool: this.state.educationSchool,
          educationDegree: this.state.educationDegree,
          educationYear: this.state.educationYear,
        },
      ],
    });
  };
  deleteEducation = (index) => {
    this.educationCounterDecrease();
    const arr = this.state.educationItems;
    arr.splice(index, 1);
    this.setState({
      educationItems: arr,
    });
  };

  educationCounterIncrease = () => {
    this.setState((prevState) => {
      return {
        ...prevState,
        educationCount: prevState.educationCount + 1,
      };
    });
  };

  educationCounterDecrease = () => {
    this.setState((prevState) => {
      return {
        ...prevState,
        educationCount: prevState.educationCount - 1,
      };
    });
  };
  educationList = () => {
    return this.state.educationItems.map((educationItem, index) => {
      return (
        <div className="education-card" key={index}>
          <i
            className="fas fa-times"
            onClick={() => this.deleteEducation(index)}
          ></i>
          <div className="education-infos">
            <h4>{educationItem.educationSchool}</h4>
            <h4>{educationItem.educationDegree}</h4>
            <h4>{educationItem.educationYear}</h4>
          </div>
        </div>
      );
    });
  };

  handleJobSubmit = (e) => {
    e.preventDefault();
    this.jobCounterIncrease();
    this.setState({
      jobCompany: "",
      jobTitle: "",
      jobYear: "",
      jobItems: [
        ...this.state.jobItems,
        {
          jobCompany: this.state.jobCompany,
          jobTitle: this.state.jobTitle,
          jobYear: this.state.jobYear,
        },
      ],
    });
  };
  deleteJob = (index) => {
    this.jobCounterDecrease();
    const arr = this.state.jobItems;
    arr.splice(index, 1);
    this.setState({
      jobItems: arr,
    });
  };

  jobCounterIncrease = () => {
    this.setState((prevState) => {
      return {
        ...prevState,
        jobCount: prevState.jobCount + 1,
      };
    });
  };

  jobCounterDecrease = () => {
    this.setState((prevState) => {
      return {
        ...prevState,
        jobCount: prevState.jobCount - 1,
      };
    });
  };

  jobList = () => {
    return this.state.jobItems.map((jobItem, index) => {
      return (
        <div className="job-card" key={index}>
          <i className="fas fa-times" onClick={() => this.deleteJob(index)}></i>
          <div className="job-infos">
            <h4>{jobItem.jobCompany}</h4>
            <h4>{jobItem.jobTitle}</h4>
            <h4>{jobItem.jobYear}</h4>
          </div>
        </div>
      );
    });
  };

  // componentDidMount() {
  //   fetch("https://restcountries.eu/rest/v2/all")
  //     .then((response) => response.json())
  //     .then((data) => {
  //       this.setState({
  //         countries: data,
  //       });
  //     });
  // }
